mapper.insert:
INSERT MESSAGE ( CHANNEL_ID
               , CONTENT
               , SENDER
               , SEND_DATE
      ) VALUES ( #{channel_id}
               , #{content}
               , #{sender}
               , #{send_date}
      )

mapper.sql:
( SELECT SEND_DATE
    FROM MESSAGE
   WHERE ID = #{cursorId}
)

mapper.select:
SELECT MESSAGE.* , EMAIL , NAME , PHONE , IFNULL(PICTURE,'/img/default-user-picture.png') AS PICTURE FROM MESSAGE LEFT JOIN MEMBER ON MEMBER.EMAIL = MESSAGE.SENDER WHERE CHANNEL_ID = #{channel_id}
AND ( SEND_DATE < OR ( SEND_DATE = AND ID < #{cursorId} ) )
ORDER BY SEND_DATE DESC , ID DESC LIMIT 30

select.foreach:
UNION ALL
( SELECT COUNT(*)
    FROM MESSAGE 
   WHERE MESSAGE.SEND_DATE > ( SELECT LAST_ACCESS_DATE
                                 FROM JOININFO
                                WHERE CHANNEL_ID  = #{item.id}
                                  AND MEMBER_EMAIL = #{item.login_email}
                             )
     AND CHANNEL_ID = #{item.id}
)

mapper.update:
UPDATE MESSAGE 
   SET DELETE_YN = 'Y' 
 WHERE ID = #{id}
