mapper.select:
select * from tasklist 
where channel_id = #{channel_id}
order by position

mapper.update:
update tasklist set


		position = (position + 1)
		where position >= #{tasklistNewIndex}
		and position < #{tasklistOldIndex}


		position = (position - 1)
		where position > #{tasklistOldIndex}
		and position <= #{tasklistNewIndex}


and channel_id = #{channelId}
